SET client_encoding = 'UTF8';
-- KINETIX PLATFORM: Mock Sanctions List Data (OFAC SDN Style)
-- 200 Mock Records for Testing Sanctions Screening

CREATE TABLE IF NOT EXISTS client_onboarding.sanctions_list (
    id SERIAL PRIMARY KEY,
    entity_name VARCHAR(255) NOT NULL,
    entity_type VARCHAR(50) NOT NULL, -- INDIVIDUAL, ENTITY, VESSEL, AIRCRAFT
    program VARCHAR(100) NOT NULL,    -- e.g., RUSSIA, IRAN, SDGT
    list_type VARCHAR(50) NOT NULL,   -- SDN, NON-SDN
    country VARCHAR(100),
    entity_address TEXT,              -- Physical or registered address
    tax_id VARCHAR(50),               -- SSN, EIN, or National ID (New)
    remarks TEXT,
    created_at TIMESTAMP WITH TIME ZONE DEFAULT CURRENT_TIMESTAMP
);

